import React, { useState, useEffect } from 'react';
import { Article } from '../types';
import { readingTracker } from '../services/readingTracker';
import { FaTimes, FaBookmark, FaExternalLinkAlt } from 'react-icons/fa';

interface ArticleReaderProps {
  article: Article;
  onClose: () => void;
}

const ArticleReader: React.FC<ArticleReaderProps> = ({ article, onClose }) => {
  const [isReading, setIsReading] = useState(false);
  const [sessionId, setSessionId] = useState<string>('');

  useEffect(() => {
    // Start reading session when component mounts
    const session = readingTracker.startReadingSession(article.id);
    setSessionId(session);
    setIsReading(true);

    return () => {
      // End reading session when component unmounts
      if (sessionId) {
        readingTracker.endReadingSession(sessionId, true);
      }
    };
  }, [article.id]);

  const formatDate = (dateString: string | undefined) => {
    if (!dateString) return 'Unknown date';
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl max-w-4xl w-full max-h-[90vh] overflow-hidden shadow-2xl">
        {/* Header */}
        <div className="bg-gradient-to-r from-purple-500 to-pink-500 text-white p-6">
          <div className="flex justify-between items-start">
            <div className="flex-1">
              <div className="flex items-center space-x-2 mb-2">
                <span className="text-sm bg-white bg-opacity-20 px-2 py-1 rounded">
                  {article.category}
                </span>
                <span className="text-sm opacity-90">
                  {article.reading_time} min read
                </span>
              </div>
              <h1 className="text-2xl font-bold leading-tight">
                {article.title}
              </h1>
            </div>
                               <button
                     onClick={onClose}
                     className="text-white hover:text-gray-200 transition-colors ml-4"
                   >
                     <FaTimes className="w-6 h-6" />
                   </button>
          </div>
        </div>

        {/* Article Content */}
        <div className="overflow-y-auto max-h-[calc(90vh-200px)]">
          <div className="p-6">
            {/* Article Meta */}
            <div className="flex items-center justify-between mb-6 pb-4 border-b border-gray-200">
              <div className="flex items-center space-x-4">
                <div className="flex items-center space-x-2">
                  <span className="text-sm text-gray-500">By</span>
                  <span className="font-medium text-gray-900">{article.author || 'Unknown Author'}</span>
                </div>
                <span className="text-gray-400">â€¢</span>
                <span className="text-sm text-gray-500">
                  {formatDate(article.published_at)}
                </span>
              </div>
              <div className="text-sm text-gray-500">
                Source: {article.source_name}
              </div>
            </div>

            {/* Article Image */}
            {article.image_url && (
              <div className="mb-6">
                <img
                  src={article.image_url}
                  alt={article.title}
                  className="w-full h-64 object-cover rounded-lg shadow-md"
                  onError={(e) => {
                    e.currentTarget.style.display = 'none';
                  }}
                />
              </div>
            )}

            {/* Article Description */}
            <div className="mb-6">
              <p className="text-lg text-gray-700 leading-relaxed italic border-l-4 border-purple-500 pl-4">
                {article.description}
              </p>
            </div>

            {/* Article Content */}
            <div className="prose prose-lg max-w-none">
              <div className="text-gray-800 leading-relaxed">
                {article.content && article.content.length > 200 ? (
                  <div className="space-y-4">
                    {/* Split content into paragraphs for better readability */}
                    {article.content.split('\n\n').map((paragraph, index) => (
                      <p key={index} className="text-lg leading-7">
                        {paragraph.trim()}
                      </p>
                    ))}
                  </div>
                ) : (
                                           <div className="text-center py-12">
                           <div className="text-4xl mb-4">ðŸ“°</div>
                           <h3 className="text-xl font-semibold text-gray-900 mb-2">Full content not available</h3>
                           <p className="text-gray-600 mb-6">
                             This article's full content is not available in our system. 
                             Click the button below to read it on the original website.
                           </p>
                           <a
                             href={article.url}
                             target="_blank"
                             rel="noopener noreferrer"
                             className="inline-flex items-center px-6 py-3 bg-gradient-to-r from-purple-500 to-pink-500 text-white rounded-lg font-medium hover:shadow-lg transition-all duration-200"
                           >
                             <FaExternalLinkAlt className="mr-2" />
                             <span>Read on {article.source_name}</span>
                           </a>
                         </div>
                )}
              </div>
            </div>

            {/* Article Tags */}
            {article.tags && article.tags.length > 0 && (
              <div className="mt-8 pt-6 border-t border-gray-200">
                <h4 className="text-sm font-medium text-gray-900 mb-3">Tags:</h4>
                <div className="flex flex-wrap gap-2">
                  {article.tags.slice(0, 10).map((tag, index) => (
                    <span
                      key={index}
                      className="px-3 py-1 bg-gray-100 text-gray-700 text-sm rounded-full"
                    >
                      #{tag}
                    </span>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Footer */}
        <div className="bg-gray-50 p-4 border-t border-gray-200">
          <div className="flex justify-between items-center">
            <div className="text-sm text-gray-500">
              Reading time: ~{article.reading_time} minutes
            </div>
            <div className="flex space-x-3">
              <button
                onClick={() => {
                  // Save to reading list
                  const saved = JSON.parse(localStorage.getItem('saved_articles') || '[]');
                  const updated = [...saved, article];
                  localStorage.setItem('saved_articles', JSON.stringify(updated));
                }}
                className="text-purple-600 hover:text-purple-700 font-medium text-sm"
              >
                ðŸ“– Save for Later
              </button>
              <a
                href={article.url}
                target="_blank"
                rel="noopener noreferrer"
                className="text-purple-600 hover:text-purple-700 font-medium text-sm"
              >
                ðŸ”— View Original
              </a>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ArticleReader;
