import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Article } from '../types';
import { newsAPI } from '../services/api';

const NewsPage: React.FC = () => {
  const { category } = useParams<{ category?: string }>();

  const [articles, setArticles] = useState<Article[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const loadArticles = async () => {
      try {
        setLoading(true);
        setError(null);

        const response = await newsAPI.getNews({ category });
        setArticles(response.articles || []);
      } catch (err) {
        console.error('Error fetching articles:', err);
        setError('Failed to load articles. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    loadArticles();
  }, [category]);

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto">
        <div className="text-center py-12">
          <div className="spinner mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading articles...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">
          {category ? `${category.charAt(0).toUpperCase() + category.slice(1)} News` : 'Latest News'}
        </h1>
        <p className="text-gray-600">
          {category 
            ? `Stay updated with the latest ${category} news and insights.`
            : 'Discover the latest news and stories from around the world.'
          }
        </p>
      </div>

      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {articles.map((article) => (
          <div key={article.id} className="bg-white rounded-lg shadow-sm border border-gray-200 article-card">
            <div className="p-6">
              <div className="flex items-center justify-between mb-3">
                <span className="text-sm font-medium text-purple-600 bg-purple-50 px-2 py-1 rounded">
                  {article.category}
                </span>
                <span className="text-sm text-gray-500">
                  {article.reading_time} min read
                </span>
              </div>

              <h3 className="text-lg font-semibold text-gray-900 mb-2 line-clamp-2">
                {article.title}
              </h3>

              <p className="text-gray-600 mb-4 line-clamp-3">
                {article.description}
              </p>

              <div className="flex items-center justify-between">
                <span className="text-sm text-gray-500">
                  {article.source_name}
                </span>
                <button className="text-purple-600 hover:text-purple-700 font-medium">
                  Read more â†’
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {articles.length === 0 && (
        <div className="text-center py-12">
          <div className="text-4xl mb-4">ðŸ“°</div>
          <h3 className="text-lg font-semibold text-gray-900 mb-2">No articles found</h3>
          <p className="text-gray-600">
            {category 
              ? `No ${category} articles available at the moment.`
              : 'No articles available at the moment.'
            }
          </p>
        </div>
      )}
    </div>
  );
};

export default NewsPage;
