import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Article } from '../types';
import { newsAPI } from '../services/api';
import { readingTracker } from '../services/readingTracker';
import ReadingTracker from '../components/ReadingTracker';
import ArticleReader from '../components/ArticleReader';
import { FaBookmark, FaArrowRight, FaSpinner, FaChartBar } from 'react-icons/fa';

const NewsPage: React.FC = () => {
  const { category } = useParams<{ category?: string }>();

  const [articles, setArticles] = useState<Article[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [showTracker, setShowTracker] = useState(false);
  const [selectedArticle, setSelectedArticle] = useState<Article | null>(null);

  useEffect(() => {
    const loadArticles = async () => {
      try {
        setLoading(true);
        setError(null);

        const response = await newsAPI.getNews({ category });
        setArticles(response.articles || []);
      } catch (err) {
        console.error('Error fetching articles:', err);
        setError('Failed to load articles. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    loadArticles();
  }, [category]);

  const handleArticleClick = (article: Article) => {
    setSelectedArticle(article);
  };

  const saveArticle = (article: Article) => {
    const saved = JSON.parse(localStorage.getItem('saved_articles') || '[]');
    const updated = [...saved, article];
    localStorage.setItem('saved_articles', JSON.stringify(updated));
  };

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto">
        <div className="text-center py-12">
          <FaSpinner className="spinner mx-auto text-purple-600" />
          <p className="mt-4 text-gray-600">Loading articles...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="max-w-7xl mx-auto">
        <div className="text-center py-12">
          <div className="text-red-600 text-lg mb-4">‚ö†Ô∏è</div>
          <p className="text-red-600 mb-4">{error}</p>
          <button 
            onClick={() => window.location.reload()} 
            className="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto">
      {/* Header with Tracker Toggle */}
      <div className="flex justify-between items-center mb-8">
        <div>
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            {category ? `${category.charAt(0).toUpperCase() + category.slice(1)} News` : 'Latest News'}
          </h1>
          <p className="text-gray-600">
            {category 
              ? `Stay updated with the latest ${category} news and insights.`
              : 'Discover the latest news and stories from around the world.'
            }
          </p>
        </div>

        <button
          onClick={() => setShowTracker(!showTracker)}
          className="bg-gradient-to-r from-purple-500 to-pink-500 text-white px-4 py-2 rounded-lg hover:from-purple-600 hover:to-pink-600 transition-all duration-200 shadow-lg flex items-center space-x-2"
        >
          <FaChartBar className="text-sm" />
          <span>{showTracker ? 'Hide' : 'Show'} Tracker</span>
        </button>
      </div>

      {/* Reading Tracker */}
      {showTracker && (
        <div className="mb-8">
          <ReadingTracker />
        </div>
      )}

      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {articles.map((article) => (
          <div 
            key={article.id} 
            data-article-id={article.id}
            className="bg-white rounded-lg shadow-sm border border-gray-200 article-card hover:shadow-lg hover:border-purple-300 hover:scale-105 transition-all duration-300 cursor-pointer group"
          >
            <div className="p-6" onClick={() => handleArticleClick(article)}>
              <div className="flex items-center justify-between mb-3">
                <span className="text-sm font-medium text-purple-600 bg-purple-50 px-2 py-1 rounded">
                  {article.category}
                </span>
                <span className="text-sm text-gray-500">
                  {article.reading_time} min read
                </span>
              </div>

              <h3 className="text-lg font-semibold text-gray-900 mb-2 line-clamp-2 group-hover:text-purple-600 transition-colors">
                {article.title}
              </h3>

              <p className="text-gray-600 mb-4 line-clamp-3">
                {article.description}
              </p>

              <div className="flex items-center justify-between">
                <span className="text-sm text-gray-500">
                  {article.source_name}
                </span>
                <div className="flex space-x-2">
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      saveArticle(article);
                    }}
                    className="text-purple-600 hover:text-purple-700 font-medium text-sm flex items-center"
                  >
                    <FaBookmark className="mr-1" />
                    <span>Save</span>
                  </button>
                  <span className="text-purple-600 hover:text-purple-700 font-medium group-hover:translate-x-1 transition-transform">
                    Read more ‚Üí
                  </span>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      {articles.length === 0 && (
        <div className="text-center py-12">
          <div className="text-4xl mb-4">üì∞</div>
          <h3 className="text-lg font-semibold text-gray-900 mb-2">No articles found</h3>
          <p className="text-gray-600">
            {category 
              ? `No ${category} articles available at the moment.`
              : 'No articles available at the moment.'
            }
          </p>
        </div>
      )}

      {/* Article Reader Modal */}
      {selectedArticle && (
        <ArticleReader
          article={selectedArticle}
          onClose={() => setSelectedArticle(null)}
        />
      )}
    </div>
  );
};

export default NewsPage;
