import React, { useState, useEffect } from 'react';
import { Article } from '../types';
import { readingTracker } from '../services/readingTracker';
import { FaBookmark, FaCheck, FaArrowRight, FaTimes, FaNewspaper } from 'react-icons/fa';

const ReadingListPage: React.FC = () => {
  const [savedArticles, setSavedArticles] = useState<Article[]>([]);
  const [readingHistory, setReadingHistory] = useState<Article[]>([]);
  const [activeTab, setActiveTab] = useState<'saved' | 'history'>('saved');

  useEffect(() => {
    // Load saved articles from localStorage
    const saved = localStorage.getItem('saved_articles');
    if (saved) {
      setSavedArticles(JSON.parse(saved));
    }

    // Load reading history from localStorage
    const history = localStorage.getItem('reading_history');
    if (history) {
      setReadingHistory(JSON.parse(history));
    }
  }, []);

  const saveArticle = (article: Article) => {
    const updated = [...savedArticles, article];
    setSavedArticles(updated);
    localStorage.setItem('saved_articles', JSON.stringify(updated));
  };

  const removeFromSaved = (articleId: number) => {
    const updated = savedArticles.filter(a => a.id !== articleId);
    setSavedArticles(updated);
    localStorage.setItem('saved_articles', JSON.stringify(updated));
  };

  const markAsRead = (article: Article) => {
    // Add to reading history
    const updatedHistory = [article, ...readingHistory];
    setReadingHistory(updatedHistory);
    localStorage.setItem('reading_history', JSON.stringify(updatedHistory));

    // Remove from saved articles
    removeFromSaved(article.id);

    // Track reading session
    const sessionId = readingTracker.startReadingSession(article.id);
    setTimeout(() => {
      readingTracker.endReadingSession(sessionId, true);
    }, 1000);
  };

  const ArticleCard: React.FC<{ article: Article; showSaveButton?: boolean; showReadButton?: boolean }> = ({ 
    article, 
    showSaveButton = false, 
    showReadButton = false 
  }) => (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 hover:shadow-lg transition-all duration-300">
      <div className="p-6">
        <div className="flex items-center justify-between mb-3">
          <span className="text-sm font-medium text-purple-600 bg-purple-50 px-2 py-1 rounded">
            {article.category}
          </span>
          <span className="text-sm text-gray-500">
            {article.reading_time} min read
          </span>
        </div>

        <h3 className="text-lg font-semibold text-gray-900 mb-2 line-clamp-2">
          {article.title}
        </h3>

        <p className="text-gray-600 mb-4 line-clamp-3">
          {article.description}
        </p>

        <div className="flex items-center justify-between">
          <span className="text-sm text-gray-500">
            {article.source_name}
          </span>
          <div className="flex space-x-2">
                               {showSaveButton && (
                     <button
                       onClick={() => saveArticle(article)}
                       className="text-purple-600 hover:text-purple-700 font-medium text-sm flex items-center"
                     >
                       <FaBookmark className="mr-1" />
                       <span>Save</span>
                     </button>
                   )}
                               {showReadButton && (
                     <button
                       onClick={() => markAsRead(article)}
                       className="text-green-600 hover:text-green-700 font-medium text-sm flex items-center"
                     >
                       <FaCheck className="mr-1" />
                       <span>Mark Read</span>
                     </button>
                   )}
            <a
              href={article.url}
              target="_blank"
              rel="noopener noreferrer"
              className="text-purple-600 hover:text-purple-700 font-medium text-sm"
            >
              Read â†’
            </a>
          </div>
        </div>
      </div>
    </div>
  );

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      {/* Header */}
      <div className="text-center mb-12">
        <h1 className="text-4xl font-bold text-gray-900 mb-4">Reading List</h1>
        <p className="text-xl text-gray-600 max-w-2xl mx-auto">
          Manage your saved articles and track your reading history. Never lose an interesting story again.
        </p>
      </div>

      {/* Tabs */}
      <div className="flex justify-center mb-8">
        <div className="bg-gray-100 rounded-lg p-1">
          <button
            onClick={() => setActiveTab('saved')}
            className={`px-6 py-2 rounded-md font-medium transition-colors ${
              activeTab === 'saved'
                ? 'bg-white text-purple-600 shadow-sm'
                : 'text-gray-600 hover:text-gray-900'
            }`}
          >
            ðŸ“š Saved Articles ({savedArticles.length})
          </button>
          <button
            onClick={() => setActiveTab('history')}
            className={`px-6 py-2 rounded-md font-medium transition-colors ${
              activeTab === 'history'
                ? 'bg-white text-purple-600 shadow-sm'
                : 'text-gray-600 hover:text-gray-900'
            }`}
          >
            ðŸ“– Reading History ({readingHistory.length})
          </button>
        </div>
      </div>

      {/* Content */}
      {activeTab === 'saved' ? (
        <div>
          {savedArticles.length === 0 ? (
            <div className="text-center py-12">
              <div className="text-6xl mb-4">ðŸ“š</div>
              <h3 className="text-xl font-semibold text-gray-900 mb-2">No saved articles yet</h3>
              <p className="text-gray-600 mb-6">
                Start saving interesting articles to read them later. They'll appear here for easy access.
              </p>
              <a
                href="/news"
                className="inline-flex items-center px-6 py-3 bg-gradient-to-r from-purple-500 to-pink-500 text-white rounded-lg font-medium hover:shadow-lg transition-all duration-200"
              >
                ðŸ“° Browse News
              </a>
            </div>
          ) : (
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
              {savedArticles.map((article) => (
                <div key={article.id} className="relative">
                  <ArticleCard article={article} showReadButton={true} />
                  <button
                    onClick={() => removeFromSaved(article.id)}
                    className="absolute top-2 right-2 w-8 h-8 bg-red-500 text-white rounded-full flex items-center justify-center hover:bg-red-600 transition-colors"
                    title="Remove from saved"
                  >
                    Ã—
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>
      ) : (
        <div>
          {readingHistory.length === 0 ? (
            <div className="text-center py-12">
              <div className="text-6xl mb-4">ðŸ“–</div>
              <h3 className="text-xl font-semibold text-gray-900 mb-2">No reading history yet</h3>
              <p className="text-gray-600 mb-6">
                Articles you read will appear here. Start reading to build your history!
              </p>
              <a
                href="/news"
                className="inline-flex items-center px-6 py-3 bg-gradient-to-r from-purple-500 to-pink-500 text-white rounded-lg font-medium hover:shadow-lg transition-all duration-200"
              >
                ðŸ“° Start Reading
              </a>
            </div>
          ) : (
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
              {readingHistory.map((article) => (
                <ArticleCard key={article.id} article={article} />
              ))}
            </div>
          )}
        </div>
      )}

      {/* Stats */}
      <div className="mt-12 bg-gradient-to-r from-purple-50 to-pink-50 rounded-2xl p-8">
        <div className="grid md:grid-cols-3 gap-6 text-center">
          <div>
            <div className="text-3xl font-bold text-purple-600">{savedArticles.length}</div>
            <div className="text-gray-600">Saved Articles</div>
          </div>
          <div>
            <div className="text-3xl font-bold text-green-600">{readingHistory.length}</div>
            <div className="text-gray-600">Articles Read</div>
          </div>
          <div>
            <div className="text-3xl font-bold text-blue-600">
              {savedArticles.length + readingHistory.length}
            </div>
            <div className="text-gray-600">Total Articles</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ReadingListPage;
